import React, { Component }  from 'react';
import {Link} from 'react-router-dom';
import ReCAPTCHA from 'react-google-recaptcha'
import "./Register.css";
import {useState} from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

function Register() {
    let navigate = useNavigate();
    const [username,setname]=useState('');
    const [useremail,setemail]=useState('');
    const [userpassword,setpassword]=useState('');
    const [load, setload] = useState(false);
     function  clickfn(){
        // if(username===''){alert("Name cannot be empty"); navigate('/register');}
        // else if(userpassword===''){alert("Password cannot be empty");navigate('/login');}
        // else if (useremail===''){alert("Email cannot be empty");navigate('/login');}
        // else{
        setload(true);
        axios.post('http://localhost:8000/api/users/signup',{
        name:username,
        email:useremail,
        password:userpassword,
        role:'Normal',
        created_by:'abh'
    })
        .then(function (response) {
            setload(false);
            alert('You have been succesfully registered. Open your mail and confirm!!!');         
        console.log(response.data);

      })
      .catch((error) => {
        setload(false);
        if (error.response) {
            console.log(error.response.data);
            console.log(error.response.status);
            console.log(error.response.headers);
        } else if (error.request) {
            console.log(error.request);
        } else {
            console.log('Error', error.message);
        }
        console.log(error.config);
    });  

    }
    return (
        <div className="container">
      {load ? (
        <div className="loader-container">
      	  <div className="spinner"></div>
        </div>
      ) : (
    <div>
         <img src="https://png.pngitem.com/pimgs/s/224-2244451_fall-always-forever-now-png-download-red-transparent.png"/>
        <form onSubmit={clickfn}>
            <label><b>Name (min 5 characters)</b></label>
            <input onChange={(e)=>{e.preventDefault(); const userName=e.target.value; setname(userName);}}  type="text" required/>
            <br/>
            <label><b>Email Address</b></label>
            <input onChange={(e)=>{e.preventDefault();const userEmail=e.target.value;setemail(userEmail);}} type="text" placeholder="Enter your email" value={useremail} required/>
            <br/>
            <label><b>Password   (min 5 characters  including 1 special character)</b></label>
            <input onChange={(e)=>{e.preventDefault();const userPassword=e.target.value;setpassword(userPassword);}} name="password" type="password" placeholder="Enter your Password" value={userpassword} required/>
            <div className="App">
      <ReCAPTCHA
        sitekey="6Ldo3pohAAAAAIIh7-J_Mtg6-HWAS3HdXpzDfmrM"
        onChange={(value)=>{console.log('Captcha value:', value);}}
      />
    </div> 
            <button type="submit" >Sign Up</button>
        </form>


        <p>Already a User? Login <Link to="/login" >here</Link></p>

    </div>
   )}
   </div>
    )

}
export default Register;
