import React, { Component } from 'react';
import "./styles.css";
import { useEffect, useState } from 'react';
import axios from 'axios';
import { useDispatch, useSelector } from "react-redux";
import { logout, selectUser } from "./features/userSlice";
import { useNavigate } from "react-router-dom";
import Navbar from './navbar';




function CreateUser() {
    const [userName, setuserName] = useState('')
    const [email, setemail] = useState('');
    const [password, setpassword] = useState('');
    const user = useSelector(selectUser);
    const dispatch = useDispatch();
    let navigate = useNavigate();
    const loggedout = (e) => {
        dispatch(logout());
        navigate('/login');
    };
    // const user = JSON.parse(localStorage.getItem("user"));
    const [taskAssignee, settaskAssignee] = useState(user.id);
    function clickfn(e) {
        e.preventDefault();
        console.log({
            title: userName,
            description: email,
            assignee: (taskAssignee),
            creator: (user.userid),
            due_date: password,
            // created_by: user.userid
        });
        axios.post('http://localhost:8000/tasks/create', {
            title: userName,
            description: email,
            assignee: (taskAssignee),
            creator: (user.userid),
            due_date: password,
            // created_by: user.userid
        }, {
            headers:
            {
                'Authorization': "Bearer " + user.verifytoken
            }
        })
            .then(function (response) {

                // alert("Task Created Succesfully");
                navigate("/dashboard");
            })
            .catch((error) => {

                if (error.response) {
                    if (error.response.data.error);
                    console.log(error.response.status);
                    console.log(error.response.headers);
                } else if (error.request) {
                    console.log(error.request);
                } else {
                    console.log('Error', error.message);
                }

            });
    }
    let xstyle = {
        borderRadius: "8px",
    };
    let zstyle = {
        borderRadius: "8px",
        margin: "10px"
    };
    let formstyle = {
        width: "40%",
        position: "relative",
        left: "450px",
        top: "80px"
    };
    return (
        <div>

            <Navbar className='navbary' dis="cu"/>
            <div style={formstyle}>
                <form onSubmit={clickfn}>
                    <label><b>Title </b></label>
                    <input style={xstyle} onChange={(e) => { e.preventDefault(); const userName = e.target.value; setuserName(userName); }} placeholder="Name" type="text" required />

                    <label><b>Description </b></label>
                    <input style={xstyle} onChange={(e) => { e.preventDefault(); const email = e.target.value; setemail(email); }} type="text" placeholder="Description of the Task" required />

                    <label><b>Assignee</b></label>
                    <input style={xstyle} onChange={(e) => { e.preventDefault(); const taskAssignee = e.target.value; settaskAssignee(taskAssignee); }} type="text" placeholder="Assignee" required />
                    <label><b>Due Date &nbsp;</b></label>


                    <input onChange={(e) => { e.preventDefault(); const newdateval = e.target.value; setpassword(newdateval); }} type="date" id="duedate" name="duedate" />
                    <button style={zstyle} type="submit">Create</button>
                </form>
            </div>
        </div>
    )
}
export default CreateUser;
