import React, { Component } from 'react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { logout, selectUser } from "./features/userSlice";
import { useNavigate } from "react-router-dom";
import ReactPaginate from 'react-paginate';
import { login } from "./features/userSlice";
function GetTasks({ itemsPerPage }) {
  const axios = require('axios');
  const [js, setjs] = useState([]);
  const [val, setval] = useState('');
  const user = useSelector(selectUser);
  let navigate = useNavigate();
  const loggedout = (e) => {
    dispatch(logout());
    navigate('/login');
  };
  useEffect(() => {
    axios.get('http://localhost:8000/tasks', {
      headers:
      {
        'Authorization': "Bearer " + user.verifytoken
      }
    })
      .then(function (response) {
        setjs(response.data);
      })
  }, []);

  return (
    <div>
      <div className='navbary'>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand" href="#"><em>&nbsp;&nbsp;DeMock</em></a>
          {/* <button className="navbar-toggler" type="button" dataToggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button> */}
          {/* <div className="collapse navbar-collapse" id="navbarNavAltMarkup"> */}
          <div className="navbar-nav">
            <button className="myTasks nav-item nav-link" onClick={(e) => { navigate('/dashboard') }}><span style={{ color: "white" }}>Dashboard</span></button>
            <p className="dis disabled" style={{ color: "white" }}>Show All Users</p>
            <button className="myTasks nav-item nav-link" onClick={(e) => { navigate('/gettasks') }}><span style={{ color: "white" }}>List Tasks</span></button>
            <button className="crtTasks nav-item nav-link" onClick={(e) => { navigate('/create/task') }}><span style={{ color: "white" }}>Create Task</span></button>
            <button className="logout nav-item nav-link" onClick={(e) => loggedout(e)}><span style={{ color: "white" }}>Logout</span></button>
          </div>
          {/* </div> */}
        </nav>
      </div>
      <input className='search' onChange={(e) => { e.preventDefault(); const finalval = e.target.value; setval(finalval); }} type="text" placeholder="Search" value={val} />
      <table>
        <tr>
          <td>Title </td>
          <td>Description</td>
          <td>Assignor</td>
          <td>Assignee</td>
          <td>Status</td>
          <td>Due Date</td>
        </tr>
        {js.filter(post => {
          if (val === '') {
            return post;
          } else if (post.title.toLowerCase().includes(val.toLowerCase())) {
            return post;
          }
          else if (post.description.toLowerCase().includes(val.toLowerCase())) {
            return post;
          }
          else if (post.created_by.toLowerCase().includes(val.toLowerCase())) {
            return post;
          }
          else if (post.assignee.toLowerCase().includes(val.toLowerCase())) {
            return post;
          }
          else if (post.status.toLowerCase().includes(val.toLowerCase())) {
            return post;
          }
          else if (post.due_date.toLowerCase().includes(val.toLowerCase())) {
            return post;
          }
        }).map((item) => {
          return (
            <tr key={item.id}>
              <th>{item.title}</th>
              <th>{item.description}</th>
              <th>{item.created_by}</th>
              <th>{item.assignee}</th>
              <th>{item.status}</th>
              <th>{item.due_date}</th>
            </tr>
          )
        })}
      </table>
    </div>
  )
}

export default GetTasks;
