import React, { Component }  from 'react';
import "./Register.css";
import {useEffect, useState} from 'react';
import axios from 'axios';
import { useDispatch, useSelector } from "react-redux";
import { selectUser } from "./features/userSlice";
import { useNavigate } from "react-router-dom";

function CreateTask() {
    const [taskTitle,settaskTitle]=useState('')
    const [taskDescription,settaskDescription]=useState('');
    const [taskAssignee, settaskAssignee] = useState(false);
    const [date_value, setdate_value]=useState(new Date());
    const user=useSelector(selectUser);
    const dispatch=useDispatch();
    let navigate = useNavigate();
    function clickfn(e){
        e.preventDefault();

     axios.post('http://localhost:8000/tasks/create',{
         title:taskTitle,
         description:taskDescription,
         assignee:taskAssignee,
         due_date:date_value,
         created_by:user.userid
        },{ headers:
            {
                'Authorization':"Bearer "+ user.verifytoken
            }})
        .then(function (response) {

   alert("Task Created Succesfully");   
   navigate("/dashboard");  
  })
  .catch((error) => {

    if (error.response) {
        if(error.response.data.error) ;
        console.log(error.response.status);
        console.log(error.response.headers);
    } else if (error.request) {
        console.log(error.request);
    } else {
        console.log('Error', error.message);
    }

});
    }

    return (
        <div>
           <form onSubmit={clickfn}>
           <label><b>Title </b></label>
            <input onChange={(e)=>{e.preventDefault(); const taskTitle=e.target.value; settaskTitle(taskTitle);}} placeholder="Title of the Task" type="text" required/>

            <label><b>Description </b></label>
            <input onChange={(e)=>{e.preventDefault();const taskDescription=e.target.value;settaskDescription(taskDescription);}} type="text" placeholder="Description of the Task" required/>

            <label><b>Assignee</b></label>
            <input onChange={(e)=>{e.preventDefault();const taskAssignee=e.target.value;settaskAssignee(taskAssignee);}}  type="text" placeholder="Assignee" required/>
            <label><b>Due Date &nbsp;</b></label>

            <input onChange={(e)=>{e.preventDefault();const newdateval=e.target.value;setdate_value(newdateval);}} type="date" id="duedate" name="duedate"/>    
            <button type="submit">Create</button>
               </form>
        </div>
    )
}
export default CreateTask;
